# In[4]:


def query_gpt(prompt):
    """Function to query GPT-4 model and return the response, with error handling."""
    try:
        response = client.chat.completions.create(
            model= "gpt-4o-mini" #"gpt-4"#"gpt-4o-mini",
            ,messages=[
                # {"role": "system", "content": "You are a helpful scientific research assistant and brainstorming AI. You are critical and grounded in scientific or prior evidence, while being open to new connections and novel leaps. You research, understand, filter and validate (or disprove) candidate novel insights, discoveries and features for researchers"},
                *icl_examples,
                {"role": "user", "content": prompt}
            ],
            max_tokens=192,
            temperature=0.8,
            stop=None
        )
        return response.choices[0].message.content.strip()
    except Exception as e:
        print(f"An error occurred: {e}")
        return "Error in generating response."

# Process each row in the dataset
results = []
for index, row in tqdm(data.iterrows(), total=data.shape[0], desc="Processing rows"):
    feature_name = row['feature_name']
    target = row['Target']
    p_val = row['p_val']
    feature_importance = row['feature_importance']
    correlation = row['correlation']

    prompt_interesting = f"Given the feature '{feature_name}' , correlation: {correlation}) related to biomedical and healthcare disease understanding, prognosis, research, and prediction of: '{target}', is this feature likely interesting and Novel/unknown to existing research? Predict 'Yes' or 'No', then explain why for a medical/clinical expert."
    prompt_plausible = f"Given the feature '{feature_name}' (p-value: {p_val}, correlation: {correlation}), SHAP feature_importance{feature_importance}, related to biomedical and healthcare disease understanding, prognosis, research, and prediction of: '{target}', does this feature make sense? Is there a plausible mechanism for the feature to affect the target (disease)? Predict 'Yes' or 'No', then explain why, for a medical/clinical expert."

    interesting_response = query_gpt(prompt_interesting)
    plausible_response = query_gpt(prompt_plausible)

    # Handle "Yes", "No", or missing cases
    def extract_response(response):
        lower_response = response.lower()
        if 'yes' in lower_response.split('.')[0].lower():
            return 'Yes'
        elif 'no' in lower_response.split('.')[0].lower():
            return 'No'
        else:
            return 'Missing'

    results.append({
        'Feature': feature_name,
        'Interesting?': extract_response(interesting_response),
        'Explanation (Interesting)': interesting_response,
        'Plausible/Makes Sense?': extract_response(plausible_response),
        'Explanation (Plausible)': plausible_response
    })

# Convert results to DataFrame for better visualization and saving
results_df = pd.DataFrame(results)


